export type BackdropProps = {
  /**
   * Blur enabled. Optional.
   * @type "true" | "false"
   * @default "true"
   * @examples
   * enableBlur="false"
   */
  enableBlur?: boolean;
  /**
   * Blur radius. Optional.
   * @type number
   * @default 100
   * @examples
   * blurIntensity={20}
   */
  blurIntensity?: number;
};
